mutation add(
    $title :String!,
    $content:String!,
    $image_url: String!
){
    banner_register(
        title : $title,
        content : $content,
        image_url: $image_url
    ) {
        id
    }
}

query detail(
    $id:String!
){
    banner_detail(
        id:$id
    ) {
        id,
        title,
        content,
        image_url
    }
}

mutation update(
    $id:String!,
