INSERT INTO course_detail (course_id,overview_text,description_text,image_url) VALUES ('e9299b5a-abd3-48f1-a544-509efceaf86a','Belajar Bahasa Indonesia','Materi-materi dalam buku ini mencakup empat unsur dasar berbahasa, yaitu mendengarkan, berbicara, membaca dan menulis, serta penambahan unsur-unsur kebahasaan dan sastra untuk melengkapi materi yang sedang dibahas. Dengan demikian, siswa diharapkan mampu menguasai dengan baik keseluruhan kompetensi yang telah ditetapkan dalam Standar Isi Tahun 2006.','http://192.168.100.62:8080/data/course/bahasa_indonesia.png');
