INSERT INTO course_detail (course_id,overview_text,description_text,image_url) VALUES ('9381cc75-a3ab-44d0-a280-0c87bc14eb30','Ciri Khusus Makhluk Hidup','Lihatlah gambar di atas. Cicak itu terjatuh ke tanah. Seekor kucing berusaha menangkapnya. Cicak kelihatan menggeliat. Ternyata, cecak melepaskan ekornya. Sesaat kemudian cecak dapat meloloskan diri dari kucing. Mengapa cicak melepas ekornya? Kalian tentu penasaran, bukan? Temukan jawabannya dengan menyimak bab ini.','https://ayoles-core.herokuapp.com/data/course/ciri_khusus_makhluk_hidup.png');
