INSERT INTO course_material (id,course_id,material_index,title) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab','9381cc75-a3ab-44d0-a280-0c87bc14eb30',2,'Bab 2 : Ciri Khusus pada Tumbuhan');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',1,'Cicak',0,'Cicak sering kita lihat di dinding dan langit-langit rumah. Cicak sangat jarang berada di lantai. Cecak bergerak dengan cara merayap. Saat merayap di dinding, cicak tidak terjatuh. Mengapa demikian? Coba bandingkan dengan hewan lainnya. Dapatkah ayam atau itik merayap di dinding? Tentu tidak, bukan?','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',2,'',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_1_1.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',3,'',0,'Ternyata, cecak memiliki perekat pada setiap ujung jari kakinya. Dengan perekat inilah kaki cecak dapat menempel di dinding. Bagaimana bila kakinya tidak bisa diangkat dari dinding karena perekat tersebut? Hal ini tidak akan terjadi. Cecak dapat mengatur banyaknya perekat yang dikeluarkan. Dengan demikian, cecak dapat tetap bergerak merayap tanpa terjatuh. Cecak dapat mendaki pohon, dinding, atau atap bangunan dengan mudah.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',4,'',0,'Selain itu, cecak mempunyai kemampuan autotomi. Cecak dapat memutuskan ekornya secara tiba-tiba. Cecak melakukan autotomi saat ditangkap mangsa. Dengan begitu, cecak dapat melarikan diri. Ekor cecak yang putus dapat tumbuh kembali.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',5,'Bebek',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_1_2.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',6,'',0,'Bebek termasuk salah satu jenis unggas. Ia hidup serta mencari makan di daratan dan perairan. Bebek menggunakan kakinya untuk berjalan. Coba bandingkan kaki bebek dengan kaki ayam. Tentu berbeda, bukan? Pada kaki bebek, setiap jarinya dihubungkan dengan selaput. Dengan kaki berselaput, bebek dapat berenang. Selain bebek, kaki berselaput juga dimiliki angsa. Sementara, ayam tidak memiliki jari berselaput. Ayam hidup dan mencari makan di darat saja.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',7,'Kelelawar',0,'Kelelawar keluar dan mencari makan pada malam hari. Sebaliknya, pada siang hari, kelelawar hanya berdiam di sarangnya. Oleh karena itu, kelelawar dijuluki hewan malam. Bagaimana kelelawar bisa menemukan makanan di kegelapan malam? Apakah kelelawar tidak tersesat atau menabrak benda-benda yang dilaluinya? Bukankah malam hari gelap gulita? Dalam keadaan gelap, kelelawar tidak pernah menabrak benda yang dilaluinya. Kelelawar juga tidak kesulitan menemukan makanan. Hal ini dikarenakan kelelawar memiliki keistimewaan. Kelelawar memiliki indra pembau dan pendengar yang tajam. Dengan penggabungan keduanya, kelelawar dapat menemukan makanan. Kelelawar dapat menentukan arah terbang dan menghindari tabrakan. ','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',8,'',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_1_2.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('6c8b9366-9f99-4dfc-9ac7-dc3a52683a98',9,'',0,'Saat terbang, kelelawar mengeluarkan bunyi tinggi yang nyaring. Bunyi ini memiliki frekuensi sangat tinggi. Bunyi ini dinamakan ultrasonik. Bunyi ultrasonik akan mengenai benda atau mangsa di sekitarnya. Bunyi ini akan dipantulkan kembali oleh benda tersebut. Kelelawar menangkap bunyi pantulan dari benda atau mangsanya. Bunyi pantulan membuat kelelawar dapat memperkirakan jarak terbang. Kelelawar dapat mengenal benda di sekitarnya. Inilah yang menjadikan kelelawar dapat membedakan antara mangsa dan bukan mangsa. Kemampuan yang dimiliki kelelawar tersebut dinamakan ekolokasi.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',1,'',0,'Tumbuhan memiliki ciri khusus yang membedakannya dengan tumbuhan lain. Ciri-ciri ini berhubungan dengan kemampuannya menyesuaikan diri terhadap lingkungan. Dengan penyesuaian diri, tumbuhan bisa bertahan hidup. Beberapa tumbuhan yang memiliki ciri khusus adalah sebagai berikut.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',2,'Mawar',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_2_1.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',3,'',0,'Tanaman mawar memiliki bunga yang indah. Namun, berhati-hatilah bila ingin memetiknya. Sebab, kalian bisa terkena duri-durinya yang tajam. Duri pada batang adalah ciri khusus yang dimiliki tanaman mawar. Duri tersebut berfungsi melindungi diri dari musuh. Musuh yang mendekat akan terkena duri tajam mawar.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',4,'Kaktus ',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_2_2.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',5,'',0,'Kaktus berasal dari daerah tandus dan bercurah hujan rendah. Kaktus memiliki bentuk beraneka macam. Ada yang berbulu seperti sikat atau batang berbintik bintik besar. Ada juga yang berbentuk batang silinder. Tumbuhan kaktus dapat hidup dalam pelbagai kondisi. Pada kondisi tandus, daun kaktus akan mengecil atau sama sekali tidak berdaun. Selain itu, batang kaktus juga menebal dan berlapis lilin. Batang tersebut berfungsi sebagai tempat penyimpanan air. ','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',6,'Kantong Semar',1,'','https://ayoles-core.herokuapp.com/data/material/mahluk_hidup_bab_2_3.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('cd99aac6-256a-4dd1-889f-5535a874ffab',7,'',0,'Kantong semar termasuk tanaman yang unik. Tanaman ini memiliki kantong di bagian ujung daun. Kantong tersebut sebenarnya adalah ujung daun yang berubah bentuk. Fungsinya untuk menangkap hewan, terutama serangga. Saat serangga masuk ke kantong, kantong segera menutup. Kantong semar kemudian mengeluarkan enzim untuk membunuh serangga. Nah, serangga inilah yang dijadikan makanan kantong semar. Kantong semar hanya mengandalkan kantongnya untuk mendapatkan makanan. Kantong semar tidak memerlukan pupuk. ','');
