INSERT INTO course_detail (course_id,overview_text,description_text,image_url) VALUES ('f0c68980-70a8-492d-8701-9fb15086dd44','Bilangan Bulat','Banyak hal di sekitar kita yang berhubungan dengan bilangan bulat. Seperti menuliskan ketinggian suatu tempat, suhu, dan keuntungan atau kerugian dalam berdagang. Ini semua berkaitan dengan bilangan bulat. Perhatikan contoh berikut ini. Ketinggian puncak Jayawijaya dan perubahan suhunya dapat ditulis menggunakan bilangan bulat. Kalian perlu memahami bilangan bulat dan operasinya dengan baik.','http://192.168.100.62:8080/data/course/bilangan_bulat.png');
