INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('5e86b760-172f-4ecd-87aa-96943a1b3752',1,'Perhatikan gambar dan bacaan berikut',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_1_1.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('5e86b760-172f-4ecd-87aa-96943a1b3752',2,'',0,'Puncak Jayawijaya merupakan gunung tertinggi di Indonesia. Gunung ini terletak di Provinsi Papua. Gunung ini merupakan anugerah dari Tuhan Yang Maha Esa karena keindahannya. Di puncak gunung tersebut terdapat salju abadi. Suhu normal di ketinggian 0 m di atas permukaan laut (dpl) adalah 370 C. Puncak Jayawijaya memiliki ketinggian 4.884 m dpl. Berdasarkan catatan BMKG (Badan Meteorologi, Klimatologi dan Geofisika) pada tanggal 6 November 2017, suhu udara puncak Jayawijaya pada pagi hari adalah –80 C, pada siang hingga sore hari –70 C, dan pada malam hari –90 C. Tahukah kamu apa yang dimaksud dengan suhu –80 C? Temukan jawabannya pada pembahasan materi ini.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('5e86b760-172f-4ecd-87aa-96943a1b3752',3,'Tokoh',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_1_2.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('5e86b760-172f-4ecd-87aa-96943a1b3752',4,'',0,'Bilangan bulat negatif pertama kali digunakan pada masa Dinasti Han. Bilangan tersebut ditulis pada buku The Nine Chapter On The Mathematical Art sekitar tahun 202-220. Dalam buku tersebut terdapat tulisan yang berisi bilangan negatif. Caranya dengan menyusun dan menghitung batang merah yang mewakili bilangan positif serta batang hitam untuk bilangan negatif. Warna merah untuk mewakili bilangan negatif dan warna hitam untuk bilangan positif. Pada abad ketiga Masehi, Diophantus juga telah menggunakan bilangan negatif.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',1,'',0,'Ada 5 tahapan yang harus kalian lakukan untuk memahami cara membaca dan menulislambang bilangan bulat. Kelima langkah tersebut adalah mengamati, menanya, menalar,mencoba, dan mengkomunikasikan.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',2,'Pengamatan 1',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_2_1.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',3,'',0,'Jayawijaya yang berubah! Ingatkah kalian suhu pada Puncak Jayawijaya pada tanggal 6 November 2017? Pada pagi hari –80C, siang hingga sore hari –70 C, dan malam hari –90 C? Bagaimana cara menuliskan pada garis bilangan? Tulis ulang bacaan di atas dengan rapi. Gunakan kalimatmu sendiri! Kerjakan di buku tugasmu!','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',4,'Pengamatan 2',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_2_2.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',5,'',0,'Makanan dan minuman disimpan dalam lemari pendingin dengan suhu tertentu. Beberapa saat, suhu makanan dan minuman dalam lemari pendingin mencapai 30 C di bawah 00 C. Suhu ini dapat dituliskan –30 C. Kemudian, makanan dan minuman dikeluarkan dari lemari pendingin. Suhu makanan dan minuman menjadi 120 C.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',6,'Bacalah dengan cermat!',0,'Bilangan bulat terdiri atas 3 macam bilangan. Bilangan tersebut adalah bulat positif, bilangan nol, dan bilangan bulat negatif.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',7,'Pengamatan 1',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_2_3.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',8,'',0,'Ubahlah setiap satuan pada gambar di atas menjadi bilangan bulat! Suhu pagi hari –8, Suhu siang hingga sore hari –7, dan Suhu malam hari –9.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',9,'',0,'Buatlah garis bilangan dengan ujung kiri dan ujung kanan diberi tanda panah. Letakkan 0 di tengah garis bilangan. Kemudian, bilangan bulat positif di sebelah kanan 0. Bilangan bulat negatif di sebelah kiri 0.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',10,'Perhatikan garis bilangan berikut!',1,'','http://192.168.100.62:8080/data/material/bilangan_bulat_bab_2_4.png');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',11,'',0,'Arti tanda panah di kedua ujung garis bilangan adalah sebagai berikut. Garis akan berlanjut untuk bilangan positif (ke kanan), juga bilangan negatif (ke kiri).','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',12,'',0,'Lengkapilah garis bilangan dengan 1 di sebelah kanan 0, 2 di sebelah kanan 1, 3 di sebelah kanan 2, 4 di sebelah kanan 3 dan seterusnya.','');
