INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',14,'',0,'Lengkapilah garis bilangan dengan lawan dari 1 yaitu –1 di sebelah kiri 0, –2 diletakkan 2 langkah disebelah kiri 0, dan seterusnya.','');
