INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',18,'Pengamatan 2',0,'Suhu 30 C di bawah 00 C dapat ditulis sebagai bilangan bulat, yaitu –3 atau dibaca negatif tiga. Pada saat suhunya 120 C di atas suhu 00 C dapat ditulis sebagai bilangan bulat 12 dan dibaca positif dua belas atau cukup dibaca dua belas.','');
INSERT INTO course_material_detail (course_material_id,position,title,type_material,content,image_url) VALUES ('518169e0-e984-4e9f-836b-93dfd02ec410',19,'Tuliskan suhu-suhu berikut pada garis bilangan bulat!',0,'a. Suhu 15 C diatas 0 C.\nb. Suhu 10 C dibawah 0 C.\nc. Arti suhu 25 C kaitannya dengan suhu di atas dan di bawah 0 C.\nd. Arti suhu –50C kaitannya dengan suhu di atas dan dibawah 0 C.\ne. Berdasarkan langkah-langkah di atas, pelajari dengan kritis materi berikut ini.','');
